var contact={init:function(){this.loadData();this.registerEvents()},registerEvents:function(){var n=$("#frm-contact").validate({errorClass:"red",rules:{name:"required",website:"required",address:"required",email:{required:!0,email:!0},phone:{required:!0,number:!0,minlength:10,maxlength:15}},messages:{name:{required:"*Please enter name"},website:{required:"*Please enter website"},address:{required:"*Please enter address"},email:{required:"*Please enter email",email:"*Please enter an accurate email"},phone:{required:"*Please enter phone number",number:"*Please enter an accurate phone number",minlength:"*Please enter an accurate phone number",maxlength:"*Please enter an accurate phone number"}}});$("#btn-save").on("click",function(){if(n.form()){var t={};t.Id=$("#txt-contact-id").val();t.Name=$("#txt-name").val();t.Website=$("#txt-website").val();t.Address=$("#txt-address").val();t.Phone=$("#txt-phone").val();t.Email=$("#txt-email").val();t.Other=$("#txt-others").val();$.ajax({type:"POST",url:"/Admin/Contact/SaveContact",data:{contactVm:t},beforeSend:common.stopLoadingIndicator(),success:function(n){n==!0?common.notify("Save success","success"):common.notify("Some fields are incorrect. Please check again.","error");common.stopLoadingIndicator()}})}})},loadData:function(){$.ajax({type:"GET",url:"/Admin/Contact/LoadContact",beforeSend:common.runLoadingIndicator(),success:function(n){$("#txt-contact-id").val(n.Id);$("#txt-name").val(n.Name);$("#txt-website").val(n.Website);$("#txt-address").val(n.Address);$("#txt-phone").val(n.Phone);$("#txt-email").val(n.Email);$("#txt-others").val(n.Other);common.stopLoadingIndicator()}})}};contact.init();