var loginController={init:function(){this.registerEvent()},registerEvent:function(){var n=$("#frmLoginForm").validate({errorClass:"red",rules:{username:"required",password:"required"},messages:{username:{required:"Username is required"},password:{required:"Password is required"}},showErrors:function(t,i){if(n.submitted){var r="";$.each(i,function(){r+="* "+this.message+"\n"});common.notify(r,"warn");submitted=!1}}});$("#btnLogin").on("click",function(n){if(n.preventDefault(),$("#frmLoginForm").valid()){var t=$("#txtUsername").val(),i=$("#txtPassword").val();loginController.login(t,i)}})},login:function(n,t){$.ajax({type:"POST",data:{Username:n,Password:t},dataType:"json",url:"/admin/login/authen",success:function(n){n.Success?window.location.href="/Admin/Home/Index":common.notify(n.Message,"error")}})}};loginController.init();